INSERT INTO `lab` VALUES (4,'先进材料设计实验室门户网站',6,'材料科学与工程学院',NULL,'lab.amd@gmail.com',NULL,'先进材料设计课题组的最大特色是跨学科的科研体系以及理论模拟与实验的有机结合。课题组的研究方向主要包括微纳制造、跨尺度的理论模拟、催化剂的可控制备技术、功能梯度材料的3D打印及设备开发等。目前课题组承担和参与国家自然科学基金、国家重大科学研究计划青年科学家专题、教育部创新团队、湖北省杰出青年、国家重点实验室自主项目、国际企业技术开发合作等项目。\n\n课题组目前共有教师3名，博士生和硕士生数十名，已经形成了一支活力蓬勃、奋发向上的年青队伍，欢迎有机械制造、机械电子、物理、材料、计算机及学科交叉背景的同学踊跃加入！欢迎来函 lab.amd@gmail.com询问。课题组的研究方向主要包括微纳制造、跨尺度的理论模拟、催化剂的可控制备技术、功能梯度材料的3D打印及设备开发等。目前课题组承担和参与国家自然科学基金、国家重大科学研究计划青年科学家专题、教育部创新团队、湖北省杰出青年、国家重点实验室自主项目、国际企业技术开发合作等项目。','http://lamd.hust.edu.cn/images/logo.png','lamd.hust.edu.cn','http://lamd.hust.edu.cn/','http://lamd.hust.edu.cn/yjcg.htm'),(5,'量子纳米材料与器件实验室',6,'材料科学与工程学院',NULL,NULL,NULL,'',NULL,'qnd.mat.hust.edu.cn',NULL,NULL),(6,'精密塑性成形研究室主页',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'ppf-mat.hust.edu.cn',NULL,NULL),(7,'华中科技大学高分子材料成形实验室',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'hscae.mat.hust.edu.cn',NULL,NULL),(8,'华中科技大学材料与工程学院介观功能与器件实验室门户网站',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'zml.mat.hust.edu.cn',NULL,NULL),(9,'华中科技大学材料科学与工程学院英文门户网站',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'english.mat.hust.edu.cn',NULL,NULL),(10,'华中科技大学材料科学与工程学院碳材料研究中心门户网站',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'carbon.mat.hust.edu.cn',NULL,NULL),(11,'华中科技大学材料科学与工程学院精材料成形与模具技术国家重点实验室门户网站',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'matproc.hust.edu.cn',NULL,NULL),(12,'非晶态材料研究组',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'bmg.mat.hust.edu.cn',NULL,NULL),(13,'动力与储能电池实验室',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'sysdoing.mat.hust.edu.cn',NULL,NULL),(14,'电化学与功能材料实验室',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'echem.mat.hust.edu.cn',NULL,NULL),(15,'材料学院电化学储能新技术实验室门户网站',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'aees.mat.hust.edu.cn',NULL,NULL),(16,'板料冲压成形与模具技术课题组',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'sheetforming.mat.hust.edu.cn',NULL,NULL),(17,'焊接与增材成形仿真研究组',6,'材料科学与工程学院',NULL,NULL,NULL,NULL,NULL,'cwamd.mat.hust.edu.cn',NULL,NULL),(18,'华中科技大学船舶与海洋工程学院307实验室',7,'船舶与海洋工程学院',NULL,NULL,NULL,NULL,NULL,'307.ch.hust.edu.cn',NULL,NULL