DROP TABLE IF EXISTS `teacher`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `teacher` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) DEFAULT NULL,
  `email` varchar(256) CHARACTER SET latin1 DEFAULT NULL,
  `honor` text,
  `address` text,
  `phone` varchar(128) CHARACTER SET latin1 DEFAULT NULL,
  `introduction` text,
  `college_name` text,
  `lab` text,
  `lab_introduction` text,
  `subject_name` text,
  `img` text CHARACTER SET latin1,
  `is_tutor` tinyint(1) DEFAULT '0',
  `is_dtutor` tinyint(1) DEFAULT '0',
  `tid` int(11) DEFAULT NULL COMMENT 'the id in teacher homepage',
  `degree` text,
  `prorank` varchar(256) DEFAULT NULL,
  `homepage` text CHARACTER SET latin1,
  `lab_website` text CHARACTER SET latin1,
  `lab_id` int(11) DEFAULT NULL,
  `lab_pintroductionlink` text COMMENT 'the introduction of the teacher in lab website',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=72 DEFAULT CHARSET=utf8 COMMENT='教师表';
